import React from "react";

const TodoFilter = (props) => {
  const yearDropdownChangeHandler = (event) => {
    props.onChangeYearFilter(event.target.value);
  };

  const statusDropdownChangeHandler = (event) => {
    props.onChangeStatusFilter(event.target.value);
  };

  return (
    <div>
      <div>
        <label>Filter by year</label>
        <select value={props.selected} onChange={yearDropdownChangeHandler}>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
      <div>
        <label>Filter by Status</label>
        <select value={props.select} onChange={statusDropdownChangeHandler}>
          <option value="Active">Active</option>
          <option value="Completed">Completed</option>
        </select>
      </div>
    </div>
  );
};

export default TodoFilter;
