import { useState } from "react";

const Todo = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [selectedStatus, setSelectedStatus] = useState(false);

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const statusChangeHandler = (event) => {
    setSelectedStatus(event.target.value);
  };

  return (
    <form onSubmit="submit">
      <div>
        <input
          type="text"
          placeholder="Name"
          name="name"
          onChange={nameChangeHandler}
          value={enteredName}
          required
        ></input>
      </div>
      <div>
        <input
          type="date"
          placeholder="Date"
          name="date"
          onChange={dateChangeHandler}
          value={enteredDate}
          required
        ></input>
      </div>
      <div>
        <select
          name="Status"
          onChange={statusChangeHandler}
          value={selectedStatus}
          required
        >
          <option value=""></option>
          <option>Active</option>
          <option>Completed</option>
        </select>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Todo;

//  Да се креира ToDo апликација, во која секоја ставка ќе има Name, Date и Status (Active, Completed).

// Секоја ставка да може да се едитира и брише.

// Листата да може да се сортира по сите колони.

// Да има филтер по Date и Status.

