import TodoDate from "./TodoDate";
import Card from "../UI/Card";

function TodoItem(props) {
  const removeTodoHandler = (enteredData) => {
    const todoData = {
      ...enteredData,
      id: enteredData.id,
    };
    props.onRemove(todoData);
  };

  return (
    <li>
      <Card>
        <div>
          <h2>{props.name}</h2>
        </div>
        <TodoDate date={props.date} />
        <div>{props.status}</div>
        <button type="button" onRemove={removeTodoHandler}>
          Delete
        </button>
      </Card>
    </li>
  );
}

export default TodoItem;
